INSERT INTO `wp_posts` VALUES (177,1,'2021-07-27 08:22:18','2021-07-27 08:22:18','<!-- wp:acf/text-with-image {\n    \"id\": \"block_60f684ffcc327\",\n    \"name\": \"acf\\/text-with-image\",\n    \"data\": {\n        \"headline\": \"Unsere Philosophie\",\n        \"_headline\": \"field_60f6839794c6a\",\n        \"text\": \"Unser Training steht für gnadenlose Effektivität. Denn es ist so authentisch wie wir selbst. Wir holen dich dort ab, wo du stehst. Und bringen dich in Topform. Bei Wind und Wetter, vor der Arbeit oder als letzte Challenge des Tages – Wir geben Vollgas. In der Kombination aus Laufen, Kraft- und Beweglichkeitstraining an frischer Luft erfährst du den speziellen Reiz unseres Trainings. Wir trainieren mitten in der Stadt. Unsere Workouts haben Hand und Fuß.\",\n        \"_text\": \"field_60f6842b94c6b\",\n        \"image\": 46,\n        \"_image\": \"field_60f6847b94c70\",\n        \"image_position\": \"right\",\n        \"_image_position\": \"field_60f91af25a687\",\n        \"button_1_text\": \"Mehr erfahren\",\n        \"_button_1_text\": \"field_60f6843494c6c\",\n        \"button_1_link\": \"\",\n        \"_button_1_link\": \"field_60f6846094c6d\",\n        \"button_1_color\": \"white\",\n        \"_button_1_color\": \"field_60f740c77a9e2\",\n        \"button_2_text\": \"Jetzt trainieren\",\n        \"_button_2_text\": \"field_60f6846b94c6e\",\n        \"button_2_link\": \"\",\n        \"_button_2_link\": \"field_60f6847594c6f\",\n        \"button_2_color\": \"brand\",\n        \"_button_2_color\": \"field_60f7410f7a9e3\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/headline {\n    \"id\": \"block_60f94894c923e\",\n    \"name\": \"acf\\/headline\",\n    \"data\": {\n        \"headline\": \"Unsere Trainer\",\n        \"_headline\": \"field_60f91e190847d\",\n        \"subline\": \"\",\n        \"_subline\": \"field_60faafc21ba00\",\n        \"color\": \"black\",\n        \"_color\": \"field_60f91e360847e\",\n        \"alignment\": \"left\",\n        \"_alignment\": \"field_60f91e4d0847f\",\n        \"push\": \"0\",\n        \"_push\": \"field_60faaefc5e897\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/image-carousel {\n    \"id\": \"block_60f9521a98b6e\",\n    \"name\": \"acf\\/image-carousel\",\n    \"data\": {\n        \"items_0_image\": 83,\n        \"_items_0_image\": \"field_60f950f94d599\",\n        \"items_0_image_alignment\": \"center center\",\n        \"_items_0_image_alignment\": \"field_60faa3c8f8148\",\n        \"items_0_headline\": \"Philipp\",\n        \"_items_0_headline\": \"field_60f950594d595\",\n        \"items_0_subline\": \"nett. laut. fußballer.\",\n        \"_items_0_subline\": \"field_60f950764d596\",\n        \"items_0_button_text\": \"mehr über Philipp\",\n        \"_items_0_button_text\": \"field_60f950804d597\",\n        \"items_0_button_color\": \"black\",\n        \"_items_0_button_color\": \"field_60f97c4b3e283\",\n        \"items_0_description\": \"<h4>PHILIPP<\\/h4>\\r\\n<em>Philipp ist Sport live. Er ist Sportwissenschaftler aus Leidenschaft, kommt von der SpoHo Köln und hat dort auch seinen Personaltrainer absolviert. Sport ist seine Berufung, Menschen auch. Empathie und Motivation pur sind seine Stärken. Er liebt klare Abmachungen und Ziele. Für seine Kunden gibt er alles. Er hat seine Berufung zum Beruf gemacht.<\\/em>\",\n        \"_items_0_description\": \"field_60f950c44d598\",\n        \"items_0_svg_color\": \"white\",\n        \"_items_0_svg_color\": \"field_60f97c993e284\",\n        \"items_1_image\": 84,\n        \"_items_1_image\": \"field_60f950f94d599\",\n        \"items_1_image_alignment\": \"center top\",\n        \"_items_1_image_alignment\": \"field_60faa3c8f8148\",\n        \"items_1_headline\": \"Yannic\",\n        \"_items_1_headline\": \"field_60f950594d595\",\n        \"items_1_subline\": \"authentisch. offen. kurzwurst.\",\n        \"_items_1_subline\": \"field_60f950764d596\",\n        \"items_1_button_text\": \"mehr über Yannic\",\n        \"_items_1_button_text\": \"field_60f950804d597\",\n        \"items_1_button_color\": \"brand\",\n        \"_items_1_button_color\": \"field_60f97c4b3e283\",\n        \"items_1_description\": \"<h4>YANNIC<\\/h4>\\r\\n<em>Sportliche Raffinesse trifft auf analytisches Denken. Eine perfekte Kombination, die für Spaß und gnadenlos gute Workouts steht. Unser Trainer absolviert derzeit sein Sportmanagement Studium in Köln, kommt aus dem Leistungssport und nimmt sich in seiner Freizeit gerne Zeit für ein gutes Buch.<\\/em>\",\n        \"_items_1_description\": \"field_60f950c44d598\",\n        \"items_1_svg_color\": \"blue\",\n        \"_items_1_svg_color\": \"field_60f97c993e284\",\n        \"items_2_image\": 81,\n        \"_items_2_image\": \"field_60f950f94d599\",\n        \"items_2_image_alignment\": \"right top\",\n        \"_items_2_image_alignment\": \"field_60faa3c8f8148\",\n        \"items_2_headline\": \"Andrea\",\n        \"_items_2_headline\": \"field_60f950594d595\",\n        \"items_2_subline\": \"emotional. ehrlich. zielstrebig.\",\n        \"_items_2_subline\": \"field_60f950764d596\",\n        \"items_2_button_text\": \"mehr über Andrea\",\n        \"_items_2_button_text\": \"field_60f950804d597\",\n        \"items_2_button_color\": \"white\",\n        \"_items_2_button_color\": \"field_60f97c4b3e283\",\n        \"items_2_description\": \"<h4>ANDREA<\\/h4>\\r\\n<em>Studium der Sportwissenschaft, Coach und Sportlerin mit Leidenschaft und Emotion. Andrea besticht durch offenes Auftreten und ihren kritischen Blick während des Workouts. Ihre langjährige Erfahrung im Outdoor Fitness Segment, Athletiktraining und der Ehrgeiz einer Leistungssportlerin spiegeln sich in ihren Trainingskonzepten wider.<\\/em>\",\n        \"_items_2_description\": \"field_60f950c44d598\",\n        \"items_2_svg_color\": \"black\",\n        \"_items_2_svg_color\": \"field_60f97c993e284\",\n        \"items_3_image\": 82,\n        \"_items_3_image\": \"field_60f950f94d599\",\n        \"items_3_image_alignment\": \"center center\",\n        \"_items_3_image_alignment\": \"field_60faa3c8f8148\",\n        \"items_3_headline\": \"Barbara\",\n        \"_items_3_headline\": \"field_60f950594d595\",\n        \"items_3_subline\": \"kreativ. fröhlich. trailrunner.\",\n        \"_items_3_subline\": \"field_60f950764d596\",\n        \"items_3_button_text\": \"mehr über Barbara\",\n        \"_items_3_button_text\": \"field_60f950804d597\",\n        \"items_3_button_color\": \"white\",\n        \"_items_3_button_color\": \"field_60f97c4b3e283\",\n        \"items_3_description\": \"<h4>BARBARA<\\/h4>\\r\\n<em>Wenn ein „Schreiner, Designer, Musiker“ zum Trainer und Ernährungscoach wird, sind Kreativität und Spaß vorprogrammiert. Barbara kam vom Fechten zum Trailrunning, weil Sport draußen einfach in jeder Hinsicht unschlagbar ist. Das Motto unserer Trainerin: Je näher an der Natur desto besser - egal ob beim Sport oder in der Küche.<\\/em>\",\n        \"_items_3_description\": \"field_60f950c44d598\",\n        \"items_3_svg_color\": \"hide\",\n        \"_items_3_svg_color\": \"field_60f97c993e284\",\n        \"items\": 4,\n        \"_items\": \"field_60f94fc44d594\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/headline {\n    \"id\": \"block_60fab14c52fa3\",\n    \"name\": \"acf\\/headline\",\n    \"data\": {\n        \"headline\": \"Die Workouts\",\n        \"_headline\": \"field_60f91e190847d\",\n        \"subline\": \"Stadt Hand Fuss\",\n        \"_subline\": \"field_60faafc21ba00\",\n        \"color\": \"black\",\n        \"_color\": \"field_60f91e360847e\",\n        \"alignment\": \"left\",\n        \"_alignment\": \"field_60f91e4d0847f\",\n        \"push\": \"1\",\n        \"_push\": \"field_60faaefc5e897\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/trainings {\n    \"id\": \"block_60fbc6b59707f\",\n    \"name\": \"acf\\/trainings\",\n    \"data\": {\n        \"items_0_headline\": \"Freistil\",\n        \"_items_0_headline\": \"field_60fbc37f5a667\",\n        \"items_0_subline\": \"laufen & springen.\",\n        \"_items_0_subline\": \"field_60fbc3985a668\",\n        \"items_0_icon\": 105,\n        \"_items_0_icon\": \"field_60fbc3a35a669\",\n        \"items_0_property_1\": \"Kondition\",\n        \"_items_0_property_1\": \"field_60fbc3fb5a66a\",\n        \"items_0_property_1_value\": \"four\",\n        \"_items_0_property_1_value\": \"field_60fbc43f5a66b\",\n        \"items_0_property_2\": \"Kraft\",\n        \"_items_0_property_2\": \"field_60fbc47d5a66c\",\n        \"items_0_property_2_value\": \"two\",\n        \"_items_0_property_2_value\": \"field_60fbc48a5a66d\",\n        \"items_0_property_3\": \"Dauer\",\n        \"_items_0_property_3\": \"field_60fbc4ad5a66e\",\n        \"items_0_property_3_value\": \"60\",\n        \"_items_0_property_3_value\": \"field_60fbc4c15a66f\",\n        \"items_0_description\": \"Wir starten zusammen, wir laufen zusammen, wir werden zusammen besser. Neben dem hohen Laufanteil achten wir auf ein gutes warm-up, Lauftechnik und Mobilitätstraining. Tempovariationen, Bergläufe, Kraftübungen und ein cool-down runden unsere Lauf- Workouts ab. Jeder hat mal angefangen, das Workout ist für alle Laufstärken geeignet.\",\n        \"_items_0_description\": \"field_60fbc5005a670\",\n        \"items_1_headline\": \"Aufbau\",\n        \"_items_1_headline\": \"field_60fbc37f5a667\",\n        \"items_1_subline\": \"sauber & technik.\",\n        \"_items_1_subline\": \"field_60fbc3985a668\",\n        \"items_1_icon\": 108,\n        \"_items_1_icon\": \"field_60fbc3a35a669\",\n        \"items_1_property_1\": \"Kondition\",\n        \"_items_1_property_1\": \"field_60fbc3fb5a66a\",\n        \"items_1_property_1_value\": \"two\",\n        \"_items_1_property_1_value\": \"field_60fbc43f5a66b\",\n        \"items_1_property_2\": \"Kraft\",\n        \"_items_1_property_2\": \"field_60fbc47d5a66c\",\n        \"items_1_property_2_value\": \"two\",\n        \"_items_1_property_2_value\": \"field_60fbc48a5a66d\",\n        \"items_1_property_3\": \"Dauer\",\n        \"_items_1_property_3\": \"field_60fbc4ad5a66e\",\n        \"items_1_property_3_value\": \"60\",\n        \"_items_1_property_3_value\": \"field_60fbc4c15a66f\",\n        \"items_1_description\": \"Wir arbeiten an den Basics. Saubere Technik ist für uns das A und O. Egal ob Liegestütz, Kniebeuge oder Burpee - hier verbessern wir gemeinsam die Ausführung oder arbeiten an neuen Variationen. Egal ob Beginner oder Profi, hier kommt jeder ins schwitzen.\",\n        \"_items_1_description\": \"field_60fbc5005a670\",\n        \"items_2_headline\": \"Zirkel\",\n        \"_items_2_headline\": \"field_60fbc37f5a667\",\n        \"items_2_subline\": \"pushen & pausieren.\",\n        \"_items_2_subline\": \"field_60fbc3985a668\",\n        \"items_2_icon\": 106,\n        \"_items_2_icon\": \"field_60fbc3a35a669\",\n        \"items_2_property_1\": \"Kondition\",\n        \"_items_2_property_1\": \"field_60fbc3fb5a66a\",\n        \"items_2_property_1_value\": \"four\",\n        \"_items_2_property_1_value\": \"field_60fbc43f5a66b\",\n        \"items_2_property_2\": \"Kraft\",\n        \"_items_2_property_2\": \"field_60fbc47d5a66c\",\n        \"items_2_property_2_value\": \"three\",\n        \"_items_2_property_2_value\": \"field_60fbc48a5a66d\",\n        \"items_2_property_3\": \"Dauer\",\n        \"_items_2_property_3\": \"field_60fbc4ad5a66e\",\n        \"items_2_property_3_value\": \"60\",\n        \"_items_2_property_3_value\": \"field_60fbc4c15a66f\",\n        \"items_2_description\": \"Wir powern uns aus. Das Workout besteht aus 10 Übungen, die Pausen sind kurz. Zusammen pushen wir uns durch den Zirkel. Du wirst unter anderem mit Medizinbällen, Battle Ropes oder nur deinem eigenen Körper trainieren. Ein intensives warm-up und Mobilitätstraining bereitet dich optimal auf den Zirkel vor. Zum Abschluss wirst du mit einem entspannten cool-down belohnt.\",\n        \"_items_2_description\": \"field_60fbc5005a670\",\n        \"items_3_headline\": \"Kraft\",\n        \"_items_3_headline\": \"field_60fbc37f5a667\",\n        \"items_3_subline\": \"effektiv & intensiv.\",\n        \"_items_3_subline\": \"field_60fbc3985a668\",\n        \"items_3_icon\": 107,\n        \"_items_3_icon\": \"field_60fbc3a35a669\",\n        \"items_3_property_1\": \"Kondition\",\n        \"_items_3_property_1\": \"field_60fbc3fb5a66a\",\n        \"items_3_property_1_value\": \"four\",\n        \"_items_3_property_1_value\": \"field_60fbc43f5a66b\",\n        \"items_3_property_2\": \"Kraft\",\n        \"_items_3_property_2\": \"field_60fbc47d5a66c\",\n        \"items_3_property_2_value\": \"five\",\n        \"_items_3_property_2_value\": \"field_60fbc48a5a66d\",\n        \"items_3_property_3\": \"Dauer\",\n        \"_items_3_property_3\": \"field_60fbc4ad5a66e\",\n        \"items_3_property_3_value\": \"60\",\n        \"_items_3_property_3_value\": \"field_60fbc4c15a66f\",\n        \"items_3_description\": \"Wir überwinden deinen inneren Schweinehund. Unser anspruchsvollstes Workout verlangt dir alles ab. Zusammen geben wir noch mehr. Komplexe Übungen (die du im Aufbau erarbeiten und verbessern kannst) bilden den Kern dieses Workouts. Wir trainieren effektiv und mit hoher Belastung. Wir starten mit einem intensiven warm-up und Mobilitätstraining, damit du im anschließenden Workout Höchstleistungen bringen kannst.\",\n        \"_items_3_description\": \"field_60fbc5005a670\",\n        \"items\": 4,\n        \"_items\": \"field_60fbc35d5a666\",\n        \"extra_box\": [\n            \"true\"\n        ],\n        \"_extra_box\": \"field_60fdbab182431\",\n        \"extra_box_headline\": \"Personal Workouts\",\n        \"_extra_box_headline\": \"field_60fdbb1682432\",\n        \"extra_box_subline\": \"<p>Neben den Workouts für Gruppen bieten wir auch Personal Trainings an. <br><small>(Preis auf Anfrage)<\\/small><\\/p>\",\n        \"_extra_box_subline\": \"field_60fdbb5282433\",\n        \"extra_box_button_text\": \"Jetzt anfragen\",\n        \"_extra_box_button_text\": \"field_60fdbb6082434\",\n        \"extra_box_button_link\": {\n            \"title\": \"Home\",\n            \"url\": \"http:\\/\\/workandout.local\\/\",\n            \"target\": \"\"\n        },\n        \"_extra_box_button_link\": \"field_60fdbb7982435\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/headline {\n    \"id\": \"block_60fe721d2e081\",\n    \"name\": \"acf\\/headline\",\n    \"data\": {\n        \"headline\": \"Partner\",\n        \"_headline\": \"field_60f91e190847d\",\n        \"subline\": \"\",\n        \"_subline\": \"field_60faafc21ba00\",\n        \"color\": \"black\",\n        \"_color\": \"field_60f91e360847e\",\n        \"alignment\": \"center\",\n        \"_alignment\": \"field_60f91e4d0847f\",\n        \"push\": \"0\",\n        \"_push\": \"field_60faaefc5e897\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/logo-carousel {\n    \"id\": \"block_60fe72122e080\",\n    \"name\": \"acf\\/logo-carousel\",\n    \"data\": {\n        \"items_0_image\": 151,\n        \"_items_0_image\": \"field_60fe6d4740c86\",\n        \"items_0_image_link\": {\n            \"title\": \"\",\n            \"url\": \"https:\\/\\/urbansportsclub.com\\/koeln1\",\n            \"target\": \"\"\n        },\n        \"_items_0_image_link\": \"field_60fe7d78fdc66\",\n        \"items_1_image\": 145,\n        \"_items_1_image\": \"field_60fe6d4740c86\",\n        \"items_1_image_link\": {\n            \"title\": \"\",\n            \"url\": \"https:\\/\\/www.holmesplace.com\\/de\\/de\\/clubs\\/am-gurzenich\",\n            \"target\": \"\"\n        },\n        \"_items_1_image_link\": \"field_60fe7d78fdc66\",\n        \"items_2_image\": 150,\n        \"_items_2_image\": \"field_60fe6d4740c86\",\n        \"items_2_image_link\": {\n            \"tit